CREATE TABLE `vitae` ( `id` INT NOT NULL AUTO_INCREMENT COMMENT '履历ID' , `admin_id` INT NOT NULL COMMENT '用户ID' , `experience` TEXT NULL COMMENT '个人经历' , `skill` TEXT NULL COMMENT '技能情况' , `contacts` INT(11) UNSIGNED NULL COMMENT '联系方式' , `recruit_time` INT(11) UNSIGNED NULL COMMENT '入职时间' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `exprience` ( `id` INT NOT NULL AUTO_INCREMENT COMMENT '个人经历' , `start_time` INT(11) NOT NULL COMMENT '开始时间' , `end_time` INT(11) NOT NULL COMMENT '结束时间' , `description` VARCHAR(30) NULL COMMENT '简述' , `location` VARCHAR(30) NOT NULL COMMENT '地点' , PRIMARY KEY (`id`)) ENGINE = InnoDB;