CREATE TABLE `vote` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '投票id' , `option_id` VARCHAR(50) NULL COMMENT '选项id' , `option_min` INT UNSIGNED NOT NULL DEFAULT '1' COMMENT '最小选择数' , `option_max` INT UNSIGNED NOT NULL DEFAULT '1' COMMENT '最小选择数' , `type` VARCHAR(20) NOT NULL DEFAULT 'ip' COMMENT '投票限制类型' , `times` INT UNSIGNED NOT NULL DEFAULT '1' COMMENT '次数' , `rate` INT NULL COMMENT '缓存刷新时间' , `start_time` INT NOT NULL COMMENT '投票开始时间' , `end_time` INT NOT NULL COMMENT '投票结束时间' , `status` TINYINT NOT NULL COMMENT '投票状态' , PRIMARY KEY (`id`)) ENGINE = InnoDB CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT = '投票表';