CREATE TABLE `vote_option` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '投票选项id' , `vote_id` INT UNSIGNED NOT NULL COMMENT '投票id' , `number` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '选项编号' , `content` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '选项内容' , `picture` VARCHAR(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '选项图片' , `sum` INT UNSIGNED NOT NULL COMMENT '选项显示数量' , `actual_sum` INT UNSIGNED NOT NULL COMMENT '选项真实数量' , PRIMARY KEY (`id`)) ENGINE = InnoDB CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT = '投票选项表';